mapper.select:
select , (select ph.item from po_header ph where ph.header_id = p.header_id and ph.organization_id = p.organization_id) as item, (select ph.item_id from po_header ph where ph.header_id = p.header_id and ph.organization_id = p.organization_id) as item_id, (select ds.dict_data_name from sys_dict_datas ds where ds.id = p.specific_id and ds.organization_id = p.organization_id and ds.dict_id = 3) as specificName from po_line p where header_id in
#{poHeaderId}
and organization_id = #{organizationId,jdbcType=BIGINT}

mapper.insert:
insert into po_line (line_id, header_id, is_ship, weight, 
  specific_id, piece_num, Unit_Price, 
  remarks, created_by, created_date, 
  last_updated_by, last_updated_date, organization_id
  )
values (#{lineId,jdbcType=BIGINT}, #{headerId,jdbcType=BIGINT},#{isShip,jdbcType=VARCHAR}, #{weight,jdbcType=DOUBLE}, 
  #{specificId,jdbcType=BIGINT}, #{pieceNum,jdbcType=BIGINT}, #{unitPrice,jdbcType=DOUBLE}, 
  #{remarks,jdbcType=VARCHAR}, #{createdBy,jdbcType=VARCHAR}, #{createdDate,jdbcType=TIMESTAMP}, 
  #{lastUpdatedBy,jdbcType=VARCHAR}, #{lastUpdatedDate,jdbcType=TIMESTAMP}, #{organizationId,jdbcType=BIGINT}
  )
